#Copiei de um maluco por ai, esse é uma calculadora de hypotenusa com janelas feias#Bom para se estudar e saber como se fazfrom tkinter import *from math import hypotdef hipo_calcular():    print("CALCULAR HIPOTENUSA!")    def calcula_tudo():        print("calculando tudo!")        hi = hypot(float(campo_cateto.get()), float(campo_cateto2.get()))        wait["text"] = "A hipotenusa\n Vale:{}".format(hi)    janela = Tk()    cateto = Label(janela, text="Informe\n O cateto oposto:")    cateto.place(x=1, y=10)    campo_cateto = Entry(janela)    campo_cateto.place(x=100, y=25)    cateto2 = Label(janela, text="Informe o\ncateto adjacente:")    cateto2.place(x=1, y=55)    campo_cateto2 = Entry(janela)    campo_cateto2.place(x=100, y=70)    wait = Button(janela, text="Digite as informações", font="ubuntu 14", command=calcula_tudo)    wait.place(x=1, y=150)    botao_calcula = Button(janela, text="Calcular!", font="ubuntu 9", command=calcula_tudo)    botao_calcula.place(x=110, y=110)    janela.geometry("300x300")    janela.mainloop(0)janela = Tk()hipo = Button(janela, text="Hipotenusa", font="ubuntu 13", width=15, command=hipo_calcular)hipo.place(x=10, y=80)janela.geometry("350x400")janela.mainloop(0)